mapper.select:
select customer_id as customerId, config_id AS configId, config_key AS configKey, config_val AS configVal, function_id AS functionId, note AS note from dict_config where config_key like concat(#{configKeyPrefix},'%')
and customer_id = #{customerId}
order by customer_id

update.foreach:
UPDATE config
SET config_val = #{dto.configVal}
WHERE customer_id = #{customerId} AND config_key = #{dto.configKey}

mapper.select:
select email from v_dim_emp where run_off_date is null
and customer_id = #{customerId}
and organization_id in
#{orgId}
AND email is not null AND email != ""
