mapper.select:
SELECT fun.function_id AS id, fun.function_key AS functionKey, fun.function_name AS `name`, fun.function_parent_id AS pid, fun.url AS urlPath, fun.isMenu, fun.show_index AS showIndex, fun.full_path AS fullPath, fun.note AS note, 0 AS type, IF(fun.function_parent_id, 'false', 'true') AS open FROM ( SELECT df.function_id, df.function_key, df.function_name, df.function_parent_id, df.url, df.is_menu AS isMenu, df.show_index, df.full_path, df.note FROM dim_function df
AND df.customer_id IN
#{cutomerId}
ORDER BY df.function_parent_id DESC,df.show_index ) fun UNION SELECT dfi.function_item_id AS id, dfi.item_code AS functionKey, dfi.note AS `name`, dfi.function_id AS pid, null AS urlPath, 0 AS isMenu, 99 AS showIndex, NULL AS fullPath, NULL AS note, 1 AS type, 'false' AS open FROM dim_function_item dfi
AND dfi.customer_id IN
#{cutomerId}

SELECT df.function_id AS id, df.function_key AS functionKey, df.function_name AS name, df.function_parent_id AS pid, df.url AS urlPath, df.is_menu AS isMenu, df.show_index AS showIndex, df.full_path AS fullPath, df.note AS note, 0 AS type FROM dim_function df
df.customer_id = #{customerId}
df.function_id = #{id}
AND df.url = #{url}
ORDER BY df.create_time LIMIT 0,1

mapper.delete:
DELETE FROM dim_function_item


    customer_id = #{customerId}


  AND function_item_id = #{itemId}


  AND function_id = #{functionId}

mapper.update:
UPDATE dim_function_item dfi


    dfi.function_id = #{itemDto.functionId},


    dfi.item_code = #{itemDto.itemCode},


    dfi.note = #{itemDto.note}




  AND  dfi.function_item_id = #{itemDto.functionItemId}


  AND  dfi.customer_id = #{itemDto.customerId}

mapper.select:
SELECT dff.function_id AS functionId, dff.function_name AS functionName, dff.function_parent_id AS pid, dff.url AS pathUrl, dff.is_menu AS isMenu, dff.show_index, NULL AS functionItem, NULL AS itemName FROM dim_function dff LEFT JOIN dim_function dff2 ON dff2.function_parent_id = dff.function_id AND dff.customer_id = dff2.customer_id
RIGHT JOIN function_role_relation frrf ON dff2.function_id = frrf.function_id AND frrf.customer_id = dff2.customer_id
dff2.customer_id = #{customerId}
AND frrf.role_id IN
#{roleId}
UNION SELECT df.function_id AS functionId, df.function_name AS functionName, df.function_parent_id AS pid, df.url AS pathUrl, df.is_menu AS isMenu, df.show_index, ( SELECT GROUP_CONCAT(dfi.item_code) FROM dim_function_item dfi WHERE dfi.function_id = df.function_id AND dfi.customer_id = #{customerId} ORDER BY dfi.modify_time ) AS functionItem, ( SELECT GROUP_CONCAT(dfi.note) FROM dim_function_item dfi WHERE dfi.function_id = df.function_id AND dfi.customer_id = #{customerId} ORDER BY dfi.modify_time ) AS itemName FROM dim_function df
RIGHT JOIN function_role_relation frr ON df.function_id = frr.function_id AND df.customer_id = frr.customer_id
df.customer_id = #{customerId}
AND frr.role_id IN
#{roleId}
ORDER BY show_index

SELECT frr.function_role_id AS roleFunctionId, frr.function_id AS functionId, frr.role_id AS roleId, frr.item_codes AS functionItem FROM function_role_relation frr
frr.role_id = #{roleId}
AND frr.customer_id = #{customerId}

mapper.update:
UPDATE dim_function df
SET df.full_path = CONCAT(#{pKey},'_',df.function_key)
WHERE df.function_parent_id = #{pid} AND df.customer_id = #{customerId}

update.foreach:
UPDATE function_config
SET is_view = #{dto.isView}, show_index = #{dto.showIndex}
WHERE function_config_id = #{dto.functionConfigId}
