mapper.select:
SELECT qmt.quota_memo_tips_id, qmt.title, qmti.quota_memo_tips_item_id, qmti.content FROM quota_memo_tips qmt LEFT JOIN quota_memo_tips_item qmti ON qmt.quota_memo_tips_id = qmti.quota_memo_tips_id AND qmt.customer_id = qmti.customer_id
AND qmt.customer_id = #{customerId}
ORDER BY qmt.show_index,qmti.show_index

SELECT qm.memo_id AS id, qm.customer_id AS customerId, qm.content AS content, qm.emp_id AS createEmpId, qm.create_time AS createTime, vde.user_name AS userName, vde.user_name_ch AS userNameCh, vde.img_path AS imgPath, IF(qms.memo_status_id IS NULL, 0, 1) isRead, IF(qm.emp_id = qms.emp_id, 1, 0) AS hasDelete FROM quota_memo qm INNER JOIN v_dim_emp vde ON vde.emp_id = qm.emp_id AND vde.customer_id = qm.customer_id LEFT JOIN quota_memo_status qms ON qm.memo_id = qms.memo_id AND qm.customer_id = qms.customer_id AND qms.emp_id = #{empId} WHERE qm.is_delete = 0
AND qm.customer_id = #{customerId}
AND qm.quota_id = #{quotaId}
AND qm.organization_id = #{organizationId}
ORDER BY qm.create_time DESC
