mapper.select:
SELECT me.messages_id AS id, me.customer_id AS customerId, me.content AS content, me.send_emp_id AS createEmpId, me.create_time AS createTime, me.type AS type, df.function_name AS target, CASE WHEN me.url is not null OR me.url!='' THEN me.url ELSE df.url END AS targetUrl, vde.user_name AS userName, vde.user_name_ch AS userNameCh, do.organization_id AS organId, do.organization_name AS organName FROM messages me INNER JOIN messages_status ms ON me.messages_id = ms.messages_id AND me.customer_id = ms.customer_id INNER JOIN v_dim_emp vde ON vde.emp_id = me.send_emp_id AND vde.customer_id = me.customer_id LEFT JOIN dim_organization do ON do.organization_id = me.organization_id AND do.customer_id = me.customer_id LEFT JOIN dim_function df ON df.function_id = me.quota_id AND df.customer_id = me.customer_id
WHERE me.is_delete = 0 AND ms.is_read=0 AND ms.emp_id = #{empId}
AND me.customer_id = #{customerId}
AND ((me.type=2 AND me.organization_id = #{organizationId}) OR me.type=1)
ORDER BY me.create_time DESC

mapper.update:
UPDATE messages_status ms INNER JOIN messages me SET ms.is_read = 1, ms.read_time = SYSDATE() WHERE me.messages_id = ms.messages_id AND me.customer_id = ms.customer_id AND ms.emp_id = #{empId} AND me.is_delete = 0 AND ms.is_read=0
AND me.customer_id = #{customerId}
AND ((me.type=2 AND me.organization_id = #{organizationId}) OR me.type=1)

    UPDATE messages_status 
    SET is_read = 1,read_time = SYSDATE() 
    WHERE 
      emp_id = #{empId}
     AND messages_id = #{messageId}
	 AND is_read=0

    AND customer_id = #{customerId}
