mapper.select:
select d.emp_hf_id as headId, d2.user_name_ch as headName, d2.email, d2.tel_phone as telPhone, GROUP_CONCAT(DISTINCT d.organization_id) as organId, GROUP_CONCAT(DISTINCT o.organization_name) as organName, GROUP_CONCAT(DISTINCT d.emp_id) as empId, GROUP_CONCAT(DISTINCT d.user_name_ch) as empName, date_format(date_add(NOW(), interval -1 day),'%Y-%m-%d') as dateStr from v_dim_emp d LEFT JOIN v_dim_emp d2 on d2.emp_id=d.emp_hf_id LEFT JOIN dim_organization o on o.organization_id=d.organization_id where d.customer_id = #{customerId} and d.emp_id in
#{id}
GROUP BY d.emp_hf_id,d2.user_name_ch,d2.email,d2.tel_phone
