mapper.select:
SELECT DISTINCT vo.organization_id, vo.organization_name, vo.organization_parent_id, vo.has_children, vo.full_path, dot.organization_type_level AS LEVEL, vo.depth, 0 as half_check FROM dict_dim_organization vo LEFT JOIN dict_dim_organization_type dot ON dot.organization_type_id = vo.organization_type_id AND vo.customer_id=dot.customer_id WHERE
vo.customer_id = #{customerId}
ORDER BY vo.depth

SELECT DISTINCT
count(e.emp_id)
FROM
v_dim_emp e
WHERE
e.run_off_date IS NULL
AND e.is_regular = 1

AND e.organization_id IN
(
SELECT t1.organization_id FROM dict_dim_organization t1
WHERE
locate(
(
SELECT t.full_path FROM dict_dim_organization t
WHERE t.organization_id = #{organizationId}
AND t.customer_id = #{customerId}
), t1.full_path
)
AND t1.customer_id = #{customerId}
)
AND e.customer_id=#{customerId}

SELECT DISTINCT vo.organization_id, vo.organization_name, vo.organization_parent_id, p.organization_name as organizationParentName, vo.has_children FROM dict_dim_organization vo LEFT JOIN dict_dim_organization p ON vo.organization_parent_id=p.organization_id AND vo.customer_id=p.customer_id WHERE (vo.customer_id = #{customerId} and vo.organization_parent_id=#{orgPid})
or vo.organization_id = #{orgPid}
order by vo.full_path

SELECT 
	t.`customer_id` AS 'customerId',
	t.`organization_id` AS 'organizationId',
	t.`organization_name` AS 'organizationName',
	t.`organization_key` AS 'organizationKey',
	t.`organization_parent_id` AS 'organizationParentId',
	p.`organization_name` AS 'organizationParentName',
	t.`depth` AS 'dept'

FROM dict_dim_organization t 
     LEFT JOIN dict_dim_organization p
        ON t.organization_parent_id=p.organization_id AND t.customer_id=p.customer_id

	t.customer_id = #{org.customerId}

		AND t.organization_id=#{org.organizationId}


		AND t.organization_name=#{org.organizationName}


		AND t.organization_key=#{org.organizationKey}


		AND t.organization_parent_id=#{org.organizationParentId}


		AND t.organization_parent_name=#{org.organizationParentName}


		AND t.depth=#{org.depth}


		AND t.organization_type_id = #{org.organizationTypeId}
