mapper.select:
    	SELECT count(1) from dim_user t 

			1=1

				AND t.emp_id <> #{empId}


				AND t.user_name = #{userName}

mapper.update:
update dim_user


        user_key = #{userKey}


        ,user_name = #{userName}


        ,user_name_ch = #{userNameCh}


        ,note = #{note}


        ,modify_user_id = #{modifyUserId}


        ,modify_time = #{modifyTime}


    	,email = #{email}


WHERE user_id = #{id} AND customer_id = #{customerId}
	and is_lock = 0

		update dim_user
        SET emp_id=#{empId} 
		WHERE user_id = #{userId} AND customer_id = #{customerId}
			and is_lock = 0

UPDATE dim_user
SET password = #{passwd}
WHERE user_id = #{userId} AND customer_id = #{customerId} 
	and is_lock = 0

UPDATE dim_user
SET password = #{passwd}
WHERE user_name = #{userName} AND customer_id = #{customerId} 
	and is_lock = 0

mapper.delete:
delete from dim_user


        customer_id = #{customerId}


        AND user_id IN

            #{id}

mapper.select:
SELECT du.user_id, du.customer_id, du.emp_id, du.user_key, du.user_name, du.user_name_ch, du.password, du.note, du.create_user_id, du.modify_user_id, du.create_time, du.modify_time FROM dim_user du INNER JOIN v_dim_emp t2 on du.emp_id = t2.emp_id
du.is_lock = 0
AND t2.organization_id in ( SELECT organization_id from role_organization_relation where role_id in
#{id}
)
AND du.customer_id = #{customerId}
AND (du.user_name LIKE concat('%',#{userName},'%') OR du.user_name_ch LIKE concat('%',#{userName},'%'))
ORDER BY du.modify_time desc limit #{rowBounds.offset} , #{rowBounds.limit}

        SELECT count(du.user_id) FROM dim_user du INNER JOIN v_dim_emp t2 on du.emp_id = t2.emp_id

        	du.is_lock = 0

                AND t2.organization_id in (
                	SELECT organization_id from role_organization_relation where role_id in

	                    #{id}

                )


               AND du.customer_id = #{customerId}


                AND (du.user_name LIKE concat('%',#{userName},'%') OR du.user_name_ch LIKE concat('%',#{userName},'%'))
